mapper.update:
UPDATE TBL_BOARD SET TITLE = #{title}, CONTENT =
#{content}
WHERE BNO = #{bno}

mapper.select:
SELECT BNO
FROM TBL_REPLY
WHERE RNO=#{rno}

mapper.update:
UPDATE TBL_BOARD
SET REPLYCNT = REPLYCNT + #{amount}
WHERE BNO=#{bno}

UPDATE TBL_BOARD
SET VIEWCNT=VIEWCNT +1
WHERE BNO = #{bno}

UPDATE TBL_COSMETIC
SET BRAND = #{brand} ,MODEL = #{model} ,PRODUCT = #{product} ,AMOUNT = #{amount} ,PRICE = #{price} ,INGREDIENT = #{ingredient} 
WHERE COSMETIC_NO = #{cosmetic_no}
